static size_t _sd2txt_len(const char *key, char *val)
{
	size_t ret = strlen(key);

	if (!*val)
		return ret;

	ret += strlen(val);
	ret++;

	return ret;
}

static void _sd2txt_count(xht_t *h, char *key, void *val, void *arg)
{
	int *count = (int *)arg;

	*count += (int)_sd2txt_len(key, (char *)val) + 1;
}

